//矩形当たり判定
int collision(int ax1, int ay1, int ax2, int ay2, int bx1, int by1, int bx2, int by2) {
	if (bx1 > ax2) { return false; }
	if (ax1 > bx2) { return false; }
	if (by1 > ay2) { return false; }
	if (ay1 > by2) { return false; }
	return true;
}

